for i in $(ls /scratch/inmegen/100g/variants-statistics/Total-variants-*); do sample=$(echo $i | rev | cut -d "-" -f 1,2 | rev) ; folder="/scratch/inmegen/100g/variants-statistics/"; total=$(cat $i); snv=$(cat ${folder}/SNV-variants-${sample}); let indel=${total}-${snv}; sam=$(echo $sample | cut -d "." -f 1); printf "${sam}\t${total}\t${snv}\t${indel}\n"; done >/scratch/inmegen/100g/variants-statistics/Table-variants.txt
